mapper.select:
SELECT * FROM (SELECT
rownum AS rn, a.* FROM games a order by release_date desc) WHERE rn
BETWEEN #{start} AND #{end}

choose.otherwise:
SELECT * FROM games
WHERE 1=1

	AND editor IN

		#{editor}



	AND REGEXP_LIKE(game_genre, #{genre})


ORDER BY ${sortBy}

SELECT * FROM games WHERE 1=1
AND editor IN
#{editor}
AND REGEXP_LIKE(game_genre, #{genre})
AND game_name LIKE '%' || #{game_name} || '%'
ORDER BY ${sortBy}
